import React, { useState } from 'react';
import styled from 'styled-components';
import HomePage from './components/HomePage';
import GoalsPage from './components/GoalsPage';
import LockedModePage from './components/LockedModePage';
import RewardsPage from './components/RewardsPage';
import MetricsPage from './components/MetricsPage';
import StatisticsPage from './components/StatisticsPage';


const AppContainer = styled.div`
  min-height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

function App() {
  const [currentPage, setCurrentPage] = useState('home');

  // Navigation function - implements visibility of system status (Nielsen's 1st heuristic)
  // and user control and freedom (Nielsen's 3rd heuristic)
  const navigateTo = (page) => {
    setCurrentPage(page);
  };

  // Page rendering based on current state
  const renderPage = () => {
    switch(currentPage) {
      case 'home':
        return <HomePage navigateTo={navigateTo} />;
      case 'goals':
        return <GoalsPage navigateTo={navigateTo} />;
      case 'lockin':
        return <LockInModePage navigateTo={navigateTo} />;
      case 'locked':
        return <LockedModePage navigateTo={navigateTo} />;
      case 'rewards':
        return <RewardsPage navigateTo={navigateTo} />;
      case 'metrics':
        return <MetricsPage navigateTo={navigateTo} />;
      case 'statistics':
        return <StatisticsPage navigateTo={navigateTo} />;
      case 'share':
        return <SharePage navigateTo={navigateTo} />;
      default:
        return <HomePage navigateTo={navigateTo} />;
    }
  };

  return (
    <AppContainer>
      {renderPage()}
    </AppContainer>
  );
}

export default App;
